mapper.select:
select
from
where 1=1
and ( PESO_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or HEAD_NAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') or OFFICE_ADDR LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and ESTA_TIME >= #{pd.lastStart}
and ESTA_TIME <= #{pd.lastEnd}
and STATE = #{pd.STATE}
order by PESO_NAME

