mapper.delete:
	delete from

	where 
		PESO_NAME = #{PESOORG_PESO_NAME}
    and
		STAFF_JOB = #{PESOORG_STAFF_JOB}
	and
		LEADER_NAME = #{PESOORG_LEADER_NAME}

mapper.select:
	select 

	from 

	where 
		PESO_NAME = #{PESOORG_PESO_NAME}
    and
		STAFF_JOB = #{PESOORG_STAFF_JOB}
	and
		LEADER_NAME = #{PESOORG_LEADER_NAME}
